23:21:45'),(3397,1,'ANSWER','CREATE',3396,3150,'2020-08-25 23:21:45'),(3399,1,'ANSWER','CREATE',3398,3174,'2020-08-25 23:21:45'),(3401,1,'ANSWER','CREATE',3400,3163,'2020-08-25 23:21:46'),(3403,1,'ANSWER','CREATE',3402,3161,'2020-08-25 23:21:46'),(3405,1,'ANSWER','CREATE',3404,3157,'2020-08-25 23:21:46'),(3407,1,'ANSWER','CREATE',3406,3178,'2020-08-25 23:21:46'),(3409,1,'ANSWER','CREATE',3408,3168,'2020-08-25 23:21:47'),(3411,1,'ANSWER','CREATE',3410,3185,'2020-08-25 23:21:47'),(3413,1,'ANSWER','CREATE',3412,3149,'2020-08-25 23:21:47'),(3415,1,'ANSWER','CREATE',3414,3170,'2020-08-25 23:21:47'),(3417,1,'ANSWER','CREATE',3416,3158,'2020-08-25 23:21:48'),(3419,1,'ANSWER','CREATE',3418,3175,'2020-08-25 23:21:48'),(3421,1,'ANSWER','CREATE',3420,3170,'2020-08-25 23:21:48'),(3423,1,'ANSWER','CREATE',3422,3191,'2020-08-25 23:21:48'),(3425,1,'ANSWER','CREATE',3424,3193,'2020-08-25 23:21:48'),(3427,1,'ANSWER','CREATE',3426,3194,'2020-08-25 23:21:49'),(3429,1,'ANSWER','CREATE',3428,3196,'2020-08-25 23:21:49'),(3431,1,'ANSWER','CREATE',3430,3152,'2020-08-25 23:21:49'),(3433,1,'ANSWER','CREATE',3432,3170,'2020-08-25 23:21:49'),(3435,1,'ANSWER','CREATE',3434,3152,'2020-08-25 23:21:50'),(3437,1,'ANSWER','CREATE',3436,3157,'2020-08-25 23:21:50'),(3439,1,'ANSWER','CREATE',3438,3162,'2020-08-25 23:21:50'),(3441,1,'ANSWER','CREATE',3440,3197,'2020-08-25 23:21:50'),(3443,1,'ANSWER','CREATE',3442,3166,'2020-08-25 23:21:51'),(3445,1,'ANSWER','CREATE',3444,3177,'2020-08-25 23:21:51'),(3447,1,'ANSWER','CREATE',3446,3204,'2020-08-25 23:21:51'),(3449,1,'ANSWER','CREATE',3448,3204,'2020-08-25 23:21:51'),(3451,1,'ANSWER','CREATE',3450,3201,'2020-08-25 23:21:52'),(3453,1,'ANSWER','CREATE',3452,3206,'2020-08-25 23:21:52'),(3455,1,'ANSWER','CREATE',3454,3207,'2020-08-25 23:21:52'),(3457,1,'ANSWER','CREATE',3456,3208,'2020-08-25 23:21:52'),(3459,1,'ANSWER','CREATE',3458,3189,'2020-08-25 23:21:52'),(3461,1,'ANSWER','CREATE',3460,3160,'2020-08-25 23:21:53'),(3463,1,'ANSWER','CREATE',3462,3152,'2020-