mapper.delete:
delete
from follow
where follower_id = #{followerId} and resource_id = #{resourceId} and resource_type=#{resourceType}

mapper.select:
select id, follower_id, resource_id, resource_type, created
from follow
where follower_id = #{followerId} and resource_id = #{resourceId} and resource_type=#{resourceType}

select id, follower_id, resource_id, resource_type, created
from follow
where follower_id = #{followerId}
and resource_type = #{resourceType}


        and resource_id in

            #{resourceId}



        and 1=2

select id, follower_id, resource_id, resource_type, created
from follow
where follower_id = #{followerId} and resource_type = #{resourceType}

    and id  <  #{cursor}

order by id desc
limit #{size}

select id, follower_id, resource_id, resource_type, created
from follow
where resource_id = #{resourceId} and resource_type = #{resourceType}

    and id  <  #{cursor}

order by id desc
limit #{size}
