mapper.insert:
insert into user (id, domain_id, version, name, password, email, avatar_url, age, gender,
                  created, description, question_count, answer_count, follower_count, followee_count
)
values (#{id}, #{domainId}, #{version}, #{name}, #{password}, #{email}, #{avatarUrl}, #{age}, #{gender},
        #{created}, #{description}, #{questionCount}, #{answerCount}, #{followerCount}, #{followeeCount}
       )
